import React, {useState, useEffect} from 'react';
import styled from "styled-components";
import axios from 'axios';
const ImgWrapper=styled.div`
    overflow-x:hidden;
    // overflow-y:hidden;
    text-align:center;
    height:300px;
    width:500px;
    position:responsive;
`;
const PostImg=styled.img`
    box-sizing: border-box;
    height:100%;
    // width:100%;
    // display:inline-block;
`;
const SlideBtn=styled.button`
    position:absolute;
    top:150px;
    ${props=>props.position==='left'?'left:10px;':'right:10px'};
    border:none;
    border-radius:50%;
    width:40px;
    height:40px;
    font-size:20px;
    color:white;
    background:  #E5E5E5;
`;
const PostHeader=styled.div`
    display:flex;
    justify-content:space-between;
    border-bottom: solid 2px #E5E5E5;
    margin:10px 0 5px 0;
    padding: 2px;
`;

const PostFooter=styled.div`
    // background: pink;
    background:white;
    display:flex;
    justify-content:space-around;
    height:60px;
    align-items:center;
    position:sticky;
    bottom:0;
`;
const PostComponent=styled.div`
    width:90%;
    margin:auto;
`;
const PostTitle=styled.div`
    display:flex;
    justify-content:space-between;
`;
const ChatButton=styled.button`
    background:#A352CC;
    color:white;
    border:none;
    width:110px;
    height:30px;
    font-size:16px;
    text-align:center;
`;
const HighLight=styled.div`
    color:#A352CC;
    font-size:20px;
    font-weight:bold;
`;
const PostButton=styled.button`
    border:none;
    background:none;
    color: #7D7D7D;
    font-size:12px;
`;
const PostContent=styled.div`
    font-size:16px; 
    margin-top:3px; 
    padding-bottom:10px;
`;
    const ShowPost=({postId})=>{
    const [imgArr,setImgArr]=useState([]);
    const [postInfo,setPostInfo]=useState('');
    
    const getPostContent=(postId)=>{
    return axios.get('https://reqres.in/api/users?page=2', {
            params: {
            page:postId
            }
        })
    };
    const getPostImg=(postId)=>{
        return axios.get('https://reqres.in/api/users?page=2', {
            params: {
            page:postId
            }
        })
    };
    useEffect((postId) => { 
    axios.all([getPostContent(postId),getPostImg(postId)]) // axios.all로 여러 개의 request를 보내고
    .then(axios.spread(({contentResp}, {imgResp}) => { // response를 spread로 받는다
        setImgArr(imgResp);

    })).catch((error) => {
        console.error(error)
    })
    }, []);
    const onClickSlide=()=>{
        alert('슬라이드 버튼 클릭');
    };
    return(
        <>
        <ImgWrapper>
        <PostImg src="img/바다.jpeg" alt="바다" />
        <PostImg src="img/빔프로젝터.jpg" alt="빔 프로젝터" />
        <PostImg src="img/빔프로젝터.jpg" alt="빔 프로젝터" />
        <PostImg src="img/바다.jpeg" alt="바다" />
        <SlideBtn onClick={onClickSlide} position="right">»</SlideBtn>
        <SlideBtn onClick={onClickSlide} position="left">«</SlideBtn>
        </ImgWrapper>
        <PostComponent>
        <PostHeader>
            <div>
            <div style={{fontWeight:'bold', fontSize:'14px'}}>사용자닉네임</div>
            <div style={{color:'#7D7D7D', fontSize:'11px'}}>사용자 주소</div>
            </div>
            <HighLight>8/18 - 8/30</HighLight>
        </PostHeader>
        <PostTitle>
        <div style={{fontWeight:'bold', fontSize:'18px', marginTop:'2px'}}>{postInfo.title}</div>
        <div>
        <PostButton>수정</PostButton>
        <PostButton>삭제</PostButton>
        </div>
        </PostTitle>
            <div style={{color:'#7D7D7D',fontSize:'12px', margin:'2px 0 0 2px'}}>{postInfo.createAt}</div>
        <PostContent>
        {postInfo.body}
        </PostContent>
        <PostFooter>
            <HighLight>{postInfo.price}</HighLight>
            <ChatButton>채팅 보내기</ChatButton>
        </PostFooter>
        </PostComponent>
        </>
    );
};
export default ShowPost;
