import React, {useState, useEffect} from 'react';
import styled from "styled-components";
import axios from 'axios';
// 이미지 슬라이더 감싸는 div
const ImgWrapper=styled.div` 
    // overflow:hidden;
    // overflow-y:hidden;
    text-align:center;
    height:300px;
    width:100%;
    position:relative;
    // transform : translateY(-100%);
    background: #E5E5E5;
`;
//이미지 하나하나 스타일링
const PostImg=styled.img`
    box-sizing: border-box;
    height:100%;
    width:100%;
    // display:inline-block;
`;
//슬라이더 양옆으로 옮기는 버튼
const SlideBtn=styled.button`
    position:absolute;
    top:150px;
    ${props=>props.name==='left'?'left:10px;':'right:10px'};
    border:none;
    border-radius:50%;
    width:35px;
    height:35px;
    font-size:20px;
    color:white;
    opacity:0.5; 
    background-color:black;
`;
//
const PostHeader=styled.div`
    display:flex;
    justify-content:space-between;
    border-bottom: solid 2px #E5E5E5;
    margin:10px 0 5px 0;
    padding: 2px;
`;

const PostFooter=styled.div`
    background:white;
    display:flex;
    justify-content:space-between;
    height:50px;
    align-items:center;
    position:fixed;
    bottom:0;
    width:90%;
`;
const PostComponent=styled.div`
    width:90%;
    height:50%;
    overflow:auto;
    margin:auto;
    position:relative;
`;
const PostTitle=styled.div`
    display:flex;
    justify-content:space-between;
`;
const ChatButton=styled.button`
    background:#A352CC;
    color:white;
    border:none;
    width:110px;
    height:30px;
    font-size:16px;
    text-align:center;
`;
const HighLight=styled.div`
    color:#A352CC;
    font-size:20px;
    font-weight:bold;
`;
const PostButton=styled.button`
    border:none;
    background:none;
    color: #7D7D7D;
    font-size:12px;
`;
const PostContent=styled.div`
    font-size:16px; 
    margin-top:3px; 
    position:relative;
    margin-bottom:15%;
`;
const ImgTurn=styled.div`
    background:black;
    width:50px;
    height:20px;
    position:absolute;
    bottom:0;
    left:46%;
    border-radius:2px;
    margin:5px;
    color:white;
    font-size:12px;
    opacity:0.5;
    text-align:center;
`;
    const ShowPost=({postId})=>{
    const [imgArr,setImgArr]=useState([]);
    const [postInfo,setPostInfo]=useState('');
    let [imgIdx,setImgIdx]=useState(0);
    alert(imgArr.length);
    // const getPostContent=(postId)=>{
    // return axios.get(url, { //통신을 위한 url을 적어주세요.
    //         params: {
    //         id:postId, //글을 클릭할때 게시글 아이디(postId)를 넘겨줘서 해당 아이디에 맞는 데이터 가져옴
    //         }
    //     });
    // };
    // const getPostImg=(postId)=>{ //통신을 위한 url을 적어주세요.
    //     return axios.get(url, {
    //         params: {
    //         id:postId, //글을 클릭할때 게시글 아이디(postId)를 넘겨줘서 해당 아이디에 맞는 데이터 가져옴
    //         }
    //     });
    // };
    // useEffect((postId) => {  //페이지가 실행될 때 한 번만 실행된다
    // axios.all([getPostContent(postId),getPostImg(postId)]) // axios.all로 여러 개의 request를 보내고
