import React, {useState, useEffect} from 'react';
import styled from "styled-components";
import axios from 'axios';
const ImgWrapper=styled.div`
    // overflow:hidden;
    // overflow-y:hidden;
    text-align:center;
    height:300px;
    width:100%;
    position:relative;
    // transform : translateY(-100%);
`;
const PostImg=styled.img`
    box-sizing: border-box;
    height:100%;
    width:100%;
    // display:inline-block;
`;
const SlideBtn=styled.button`
    position:absolute;
    top:150px;
    ${props=>props.name==='left'?'left:10px;':'right:10px'};
    border:none;
    border-radius:50%;
    width:35px;
    height:35px;
    font-size:20px;
    color:white;
    opacity:0.5; 
    background-color:black;
`;
const PostHeader=styled.div`
    display:flex;
    justify-content:space-between;
    border-bottom: solid 2px #E5E5E5;
    margin:10px 0 5px 0;
    padding: 2px;
`;

const PostFooter=styled.div`
    background:white;
    display:flex;
    justify-content:space-between;
    height:50px;
    align-items:center;
    position:fixed;
    bottom:0;
    width:90%;
`;
const PostComponent=styled.div`
    width:90%;
    height:50%;
    overflow:auto;
    margin:auto;
    position:relative;
`;
const PostTitle=styled.div`
    display:flex;
    justify-content:space-between;
`;
const ChatButton=styled.button`
    background:#A352CC;
    color:white;
    border:none;
    width:110px;
    height:30px;
    font-size:16px;
    text-align:center;
`;
const HighLight=styled.div`
    color:#A352CC;
    font-size:20px;
    font-weight:bold;
`;
const PostButton=styled.button`
    border:none;
    background:none;
    color: #7D7D7D;
    font-size:12px;
`;
const PostContent=styled.div`
    font-size:16px; 
    margin-top:3px; 
    position:relative;
    margin-bottom:15%;
`;
const ImgTurn=styled.div`
    background:black;
    width:50px;
    height:20px;
    position:absolute;
    bottom:0;
    left:46%;
    border-radius:2px;
    margin:5px;
    color:white;
    font-size:12px;
    opacity:0.5;
    text-align:center;
`;
    const ShowPost=({postId})=>{
    const [imgArr,setImgArr]=useState([]);
    const [postInfo,setPostInfo]=useState('');
    let [imgIdx,setImgIdx]=useState(0);
    const getPostContent=(postId)=>{
    return axios.get(url, { //통신을 위한 url을 적어주세요.
            params: {
            id:postId, //글을 클릭할때 게시글 아이디(postId)를 넘겨줘서 해당 아이디에 맞는 데이터 가져옴
            }
        });
    };
    const getPostImg=(postId)=>{ //통신을 위한 url을 적어주세요.
        return axios.get(url, {
            params: {
            id:postId, //글을 클릭할때 게시글 아이디(postId)를 넘겨줘서 해당 아이디에 맞는 데이터 가져옴
            }
        });
    };
    useEffect((postId) => {  //페이지가 실행될 때 한 번만 실행된다
    axios.all([getPostContent(postId),getPostImg(postId)]) // axios.all로 여러 개의 request를 보내고
    .then(axios.spread((contentResp, imgResp) => { // response를 spread로 받는다
        //contentResp: 이미지를 제외한 게시물 정보 데이터 / imgResp: 이미지 데이터
        setPostInfo(contentResp); 
        setImgArr(imgResp);
    })).catch((error) => {
        console.error(error)
    })
    }, []);
const onClickSlide=(e)=>{
    if(e.target.name==='right'&&imgArr.length-1!==imgIdx){
        setImgIdx(imgIdx+1);
       console.log(e.target.name);
    }//슬라이더 오른쪽 버튼 누르면, 마지막 인덱스가 아닌지 확인후 다음 인덱스의 이미지 가져옴
 else if(e.target.name==='left'&&imgIdx>0){
    setImgIdx(imgIdx-1);
    console.log(e.target.name);
}//슬라이더 왼쪽 버튼 누르면, 처음 인덱스가 아닌지 확인후 다음 인덱스의 이미지 가져옴
};
    return(
        <>
        <ImgWrapper>
            <PostImg src={imgArr[imgIdx].url} alt={imgArr[imgIdx].name}/>
        <SlideBtn name="right" onClick={onClickSlide}>»</SlideBtn>
        <SlideBtn name="left" onClick={onClickSlide}>«</SlideBtn>
        <ImgTurn>
           <span style={{opacity:'1.0'}}>{imgIdx+1} / {imgArr.length}</span> 
        </ImgTurn>
        </ImgWrapper>
        <PostComponent>
        <PostHeader>
            <div>
            <div style={{fontWeight:'bold', fontSize:'14px'}}>
                {postInfo.user.nick}
                </div>
            <div style={{color:'#7D7D7D', fontSize:'11px'}}>
                {postInfo.user.address}
                </div>
            </div>
            <HighLight>{postInfo.startDate} - {postInfo.endDate}</HighLight>
        </PostHeader>
        <PostTitle>
        <div style={{fontWeight:'bold', fontSize:'18px', marginTop:'2px'}}>{postInfo.title}</div>
        <div>
        <PostButton>수정</PostButton>
        <PostButton>삭제</PostButton>
        </div>
        </PostTitle>
            <div style={{color:'#7D7D7D',fontSize:'12px', margin:'2px 0 0 2px'}}>{postInfo.createAt}</div>
        <PostContent>
        {postInfo.body} 
        </PostContent>
        <PostFooter>
            <HighLight>20,000원
                {postInfo.price}
            </HighLight>
            <ChatButton>채팅 보내기</ChatButton>
        </PostFooter>
        </PostComponent>
        </>
    );
};
export default ShowPost;
