import React, {useState} from 'react';
import styled from "styled-components";
const PostImg=styled.img`
    height:300px;
    width:100%;
`;
const PostHeader=styled.div`
    display:flex;
    justify-content:space-between;
    width:90%;
    border-bottom: solid 2px #E5E5E5;
    margin:auto;
    margin-bottom:5px;
    padding: 2px;
`;

const PostFooter=styled.div`
    // background: pink;
    background:white;
    display:flex;
    justify-content:space-around;
    height:60px;
    align-items:center;
    position:sticky;
    bottom:0;
`;
const ChatButton=styled.button`
    background:#A352CC;
    color:white;
    border:none;
    width:110px;
    height:30px;
    font-size:16px;
    text-align:center;
`;
const HighLight=styled.div`
    color:#A352CC;
    font-size:20px;
    font-weight:bold;
`;
const PostButton=styled.button`
    border: solid 2px #E5E5E5;
    background:none;
`;
    const ShowPost=()=>{
    const [img,setImg]=useState('');
    const [nickname,setNickname]=useState('');
    
    return(
        <>
        <PostImg src="img/빔프로젝터.jpg" alt="빔 프로젝터" />
        <PostHeader>
            <div>유저닉네임</div>
            <div>미추홀구 용현동</div>
            <HighLight>8/18 - 8/30</HighLight>
        </PostHeader>
            <span>빔프로젝터 빌려드립니다.</span>
            <PostButton>수정</PostButton>
            <PostButton>삭제</PostButton>
            <div>2021.07.28 17:00</div>
        <PostFooter>
            <HighLight>20,000원</HighLight>
            <ChatButton>채팅 보내기</ChatButton>
        </PostFooter>
        
        </>
    );
};
export default ShowPost;
