import React, {useState, useEffect} from 'react';
import styled from "styled-components";
import axios from 'axios';
const ImgWrapper=styled.div`
    // overflow:hidden;
    // overflow-y:hidden;
    text-align:center;
    height:300px;
    width:100%;
    position:responsive;
    // transform : translateY(-100%);
`;
const PostImg=styled.img`
    box-sizing: border-box;
    height:100%;
    width:100%;
    // display:inline-block;
`;
const SlideBtn=styled.button`
    position:absolute;
    top:150px;
    ${props=>props.name==='left'?'left:10px;':'right:10px'};
    border:none;
    border-radius:50%;
    width:35px;
    height:35px;
    font-size:20px;
    color:white;
    opacity:0.5; 
    background-color:black;
`;
const PostHeader=styled.div`
    display:flex;
    justify-content:space-between;
    border-bottom: solid 2px #E5E5E5;
    margin:10px 0 5px 0;
    padding: 2px;
`;

const PostFooter=styled.div`
    // background: pink;
    background:white;
    display:flex;
    justify-content:space-between;
    height:60px;
    align-items:center;
    position:sticky;
    bottom:0;
`;
const PostComponent=styled.div`
    width:90%;
    margin:auto;
`;
const PostTitle=styled.div`
    display:flex;
    justify-content:space-between;
`;
const ChatButton=styled.button`
    background:#A352CC;
    color:white;
    border:none;
    width:110px;
    height:30px;
    font-size:16px;
    text-align:center;
`;
const HighLight=styled.div`
    color:#A352CC;
    font-size:20px;
    font-weight:bold;
`;
const PostButton=styled.button`
    border:none;
    background:none;
    color: #7D7D7D;
    font-size:12px;
`;
const PostContent=styled.div`
    font-size:16px; 
    margin-top:3px; 
    padding-bottom:10px;
`;

    const ShowPost=({postId})=>{
    const [imgArr,setImgArr]=useState([
        {name:'바다', url:'img/바다.jpeg'},
        {name:'빔프로젝터', url:'img/빔프로젝터.jpg'},
        {name:'바다', url:'img/바다.jpeg'},
        {name:'빔프로젝터', url:'img/빔프로젝터.jpg'},
    ]);
    const [postInfo,setPostInfo]=useState('');
    let [imgIdx,setImgIdx]=useState(0);
    // const getPostContent=(postId)=>{
    // return axios.get(url, {
    //         params: {
    //         page:postId
    //         }
    //     });
    // };
    // const getPostImg=(postId)=>{
    //     return axios.get(url, {
    //         params: {
    //         page:postId
    //         }
    //     });
    // };
    // useEffect((postId) => { 
    // axios.all([getPostContent(postId),getPostImg(postId)]) // axios.all로 여러 개의 request를 보내고
    // .then(axios.spread((contentResp, imgResp) => { // response를 spread로 받는다
    //     setPostInfo(contentResp);
    //     setImgArr(imgResp);
    // })).catch((error) => {
    //     console.error(error)
    // })
    // }, []);

//     const onClickSlide=(e)=>{
//         if(e.target.name==='right'&&imgArr.length-1!==imgIdx){
//             setImgIdx(imgIdx+1);
//            console.log(e.target.name);
//      }
//      else if(e.target.name==='left'&&imgIdx>0){
//         setImgIdx(imgIdx-1);
//        console.log(e.target.name);
//  }
//     };
const onClickSlide=(e)=>{
    if(e.target.name==='right'&&imgArr.length-1!==imgIdx){
        setImgIdx(imgIdx+1);
       console.log(e.target.name);
 }
 else if(e.target.name==='left'&&imgIdx>0){
    setImgIdx(imgIdx-1);
   console.log(e.target.name);
}
};
    return(
        <>
        <ImgWrapper>
        {/* {imgArr&&imgArr.map((data) => {
            return (<PostImg
                        src={data.url} alt={data.name}
                        key={data.id}
                        />);
            })}; */}
            <PostImg
                        src={imgArr[imgIdx].url} alt={imgArr[imgIdx].name}

                        />
        <SlideBtn name="right" onClick={onClickSlide}>»</SlideBtn>
        <SlideBtn name="left" onClick={onClickSlide}>«</SlideBtn>
        </ImgWrapper>
        <PostComponent>
        <PostHeader>
            <div>
            <div style={{fontWeight:'bold', fontSize:'14px'}}>
                사용자 닉네임{/* {postInfo.user.nick} */}
                </div>
            <div style={{color:'#7D7D7D', fontSize:'11px'}}>
                미추홀구 용현동{/* {postInfo.user.address} */}
                </div>
            </div>
            <HighLight>8/18{postInfo.firstDate} - 8/30{postInfo.endDate}</HighLight>
        </PostHeader>
        <PostTitle>
        <div style={{fontWeight:'bold', fontSize:'18px', marginTop:'2px'}}>{postInfo.title}</div>
        <div>
        <PostButton>수정</PostButton>
        <PostButton>삭제</PostButton>
        </div>
        </PostTitle>
            <div style={{color:'#7D7D7D',fontSize:'12px', margin:'2px 0 0 2px'}}>2021.08.{postInfo.createAt}</div>
        <PostContent>
        {postInfo.body}
        </PostContent>
        <PostFooter>
            <HighLight>20,000원
                {postInfo.price}
            </HighLight>
            <ChatButton>채팅 보내기</ChatButton>
        </PostFooter>
        </PostComponent>
        </>
    );
};
export default ShowPost;
