import React, {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Rate } from 'antd';
import {ReviewButton, PostHeader} from './ShowPost';
import { ArrowLeftOutlined } from '@ant-design/icons';
const ReviewWrite=()=> {
  
    const [currentValue, setCurrentValue] = useState(0)
    
    return (
    <>
      <PostHeader style={{border:'none', padding:'20px', textAlign:'center', margin:'auto'}}>
        <ArrowLeftOutlined />
        <h4 style={{fontStyle:'bold', fontSize:'18px'}}>대여후기 작성</h4>
        <ReviewButton style={{display:'inline-block', fontSize:'15px',height:'25px', textAlign:'center', padding:'auto'}}type="primary">완료</ReviewButton>    
      </PostHeader>
      <Rate allowHalf onChange={(value) => {
        setCurrentValue(value)
      }} value={currentValue} style={{color:'#EBCAFD'}}/> <br />
      Current Rating: {currentValue}
      <p>별을 탭하여 평가해주세요</p>
      </>
    );
  }

export default ReviewWrite;
// #EBCAFD 연한 보라색
