import React, {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import { Link, useHistory } from 'react-router-dom';
import 'antd/dist/antd.css';
import axios from 'axios';
import { List, Avatar, Button, Skeleton, Rate } from 'antd';
import {ReviewButton, PostHeader} from './ShowPost';
import { ArrowLeftOutlined } from '@ant-design/icons';
import profile from './images/profile.png'

const ReviewList=()=> {

    const count = 3;
    const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;
    const [initLoading,setInitLoading]= useState(true);
    const [loading,setLoading]=useState(false);
    const [data,setData]=useState([]);
    const [list,setList]=useState([]);
      
    //시작할 때 실행
    useEffect(()=>{
        axios.get(fakeDataUrl)
          .then(res => {
            setInitLoading(false);
            setData(res.data.results);
            setList(res.data.results);
            }).catch((error)=>{
                console.log(error);
            })},[]);
    

    const onLoadMore = () => {
          setLoading(true);
          setList(data.concat(
            [...new Array(count)].map(() => ({ loading: true, name: {}})),
          ));
        axios.get(fakeDataUrl)
          .then(res => {
            const newData=data.concat(res.data.results);
            setData(newData);
            setList(newData);
            setLoading(false);
          }).catch(error=>{
            console.log(error)
          })
                // Resetting window's offsetTop so:to display react-virtualized demo underfloor.
                // In real scene, you can using public method of react-virtualized:
                // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
                window.dispatchEvent(new Event('resize'));
          };
      
     
      const loadMore =
       !(initLoading &&loading) ?
          (<div
            style={{
              textAlign: 'center',
              marginTop: 12,
              height: 32,
              lineHeight: '32px',
            }}
          >
            <Button onClick={onLoadMore}>빌린 후기 더보기</Button>
          </div>)
          :null;

    return (
        <List
          className="demo-loadmore-list"
          loading={initLoading}
          itemLayout="horizontal"
          loadMore={loadMore}
          dataSource={list}
          renderItem={item => (
            <List.Item
              actions={[<a key="list-loadmore-edit">수정</a>, <a key="list-loadmore-more">삭제</a>]}
            >
              <Skeleton avatar title={false} loading={item.loading} active>
                <List.Item.Meta
                  avatar={<Avatar src={profile} />}
                  title={
                    <div>
                  <span style={{fontWeight:'bold'}}>익명</span>
                  <Rate style={{color:'#EBCAFD', fontSize:'16px'}} disabled defaultValue={2} />
                  </div>
                }
                  description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                />
              </Skeleton>
            </List.Item>
          )}
          style={{marginBottom:'100px'}}
        />
    );
 }

 export default ReviewList;
