import React  from 'react';
import styled from "styled-components";
// 게시글 하나 전체를 묶는 div 태그의 스타일링
const Post=styled.div`
    border-bottom: solid 2px #E5E5E5;
    border-radius:20px;
    height:120px;
    width:95%;
    margin:auto;
    box-sizing: border-box;
    padding:15px;
    margin-bottom:5px;
    background:white;
    position:relative;
    &:hover { //게시글 위에 커서 올릴경우
        background:#E5E5E5;
    }
`;
//이미지 스타일링
const PostImg=styled.div`
    float:left;
    display: flex;
    justify-content:space-between;
    margin-right: 10px;
    margin-bottom:3px;
`;

//게시글 제일 상단(글 제목, 작성날짜) 스타일링
const PostHeader=styled.div`
    display: flex;
    justify-content:space-between;
    margin-bottom:3px;
`;
// 게시글 제일 하단 (대여기간, 대여비) 스타일링
const PostFooter=styled.div`
    position:relative;
    // right:10px;
    top:20px;
    color:#A352CC;
    font-weight: bold;
    font-size:18px;
    display: flex;
    justify-content:space-between;
`;

// [변수 이름]
// image: 이미지 주소
// title: 게시글 제목
// postDate: 게시글 작성날짜/시간
// writerAddress: 작성자 주소
// startDate: 대여기간 시작날
// endDate: 대여기간 마지막날
// cost: 대여비용

const PostComponent2=({image, title, postDate, writerAddress, startDate,endDate,cost})=>{
    return(
        <Post>
            <PostImg>
                <div><img src={`http://localhost:7000${image}`} alt='' style={{width:'100px', height:'100px', 'border-radius':'10px'}}></img></div>
            </PostImg>
                    <PostHeader>
                        <div style={{color:'#000000',
                        fontSize:'18px', fontWeight:'bold'}}>{title}</div>
                        <div style={{color:'#7D7D7D',
                        fontSize:'12px'}}>{postDate}</div>
                        </PostHeader>
                        <div style={{color:'#7D7D7D',
                        fontSize:'12px', fontWeight:'bold', 'text-align': 'left'}}>{writerAddress}</div>
                        <PostFooter>
                            <div style={{fontSize:'16px'}}>{`${startDate} - ${endDate}`}</div>
                            <div>{cost}원</div>
                        </PostFooter>
        </Post>
    )
};

export default PostComponent2
