import React, {useCallback, useState, useEffect} from 'react';
import Logo from './Logo';
import axios from 'axios';
import Postcode from '@actbase/react-daum-postcode';
import Modal from 'react-modal';
import styled from "styled-components";
import {DownOutlined, UpOutlined } from '@ant-design/icons';
const Page=styled.body`
    margin:0;
    padding:20px;
    display:flex;
    flex-direction:column;
    align-items:center;
`;
const InputComponent=styled.input`
    border:none;
    outline:none;
    border-bottom: 1px solid #E5E5E5;
    ::placeholder{
        color:#E5E5E5;
    }
    width:250px;
    height:40px;
    margin-bottom:10px;
`;
const Label=styled.h2`  
font-size:14px;
color:#7D7D7D;
font-weight:600;
`;

const SubmitButton=styled.button`
    margin-top:30px;
    border:none;
    width:350px;
    height:50px;
    background-color:#A352CC;
    border-radius:5px;
    color:white;
    font-weight:bold;
    font-size:18px;
    text-align:center;
`;
const Btn=styled.button`
    border: 1px solid #EBCAFD;
    background-color:white;
    color:#EBCAFD;
    width:80px;
    height:30px;
    border-radius:5px;
    margin-left:12px;
`;

const CloseBtn=styled.div`
     position:fixed;
    top:0;
    right:0;
    height:20px;
    margin:5px 10px 0px 0px;
    padding:auto;
    background:none;
    color:#E5E5E5;
    width:20px;
    font-weight:bold;
    text-align:center;
    border-radius:2px;
    cursor:pointer;
`;
const Head = styled.div`
    background-color:#A352CC;
    height:60px;
    text-align:center;
    color:#ffffff;
    display:flex;
    justify-content:center;
    align-items:center;
    font-weight:bold;
    font-size:20px; 
`;
const MyInfoEdit=()=>{
    const useInput = (initValue = null) =>{
        const [value,setter] = useState(initValue);
       
        const handler = (e) => {
            if(e) setter(e.target.value);
            else setter(initValue);
        };
        return [value,handler];
    };
   
    
    const [email,setEmail] = useInput('');
    const [id, setId]=useInput('sychoi0516');
    const [pw,setPw] = useInput('');
    const [nickname,setNickname] = useInput('dkssud');
    const [showAddress,setShowAddress] =  useState('');
    const [fullAddress,setFullAddress]=useState('');
    //주소의 경우, 사용자에게 보여주는 것은 showAddress로 '##동'으로만 나오고, full 주소는 fullAddress에 저장됩니다.
    const [pwChk,setPwChk]=useState('');
    const [pwError,setPwError]=useState(false);
    const [chkNickname,setChkNickname]=useState(false);
    // const pwChkRef=useRef();
    // const pwRef=useRef();
    const [ showPwd, setShowPwd ] = useState(false) //비밀번호 설정 칸 보이는 여부



      // 유저 정보 가져오기
      const getUser = () => {
        return axios.get("http://localhost:7000/auth", { withCredentials: true })
    };
    useEffect(()=>{
        axios.get("http://localhost:7000/auth", { withCredentials: true })
        .then((res)=>{
            if(res.data){
                setEmail(res.data.email);
            }
        }).catch((error)=>{
            console.log(error);
        }

        );
    },[]);
   
    const onSubmitForm=(e)=>{
        e.preventDefault();
        if(!(nickname&&fullAddress)){
            alert(`모든 문항을 입력해주세요`);
            return;
        }
        if(showPwd&&!(pw&&pwChk)){
            alert(`변경할 비밀번호를 입력해주세요`);
            return;
        }
        else{
        //  입력하지 않은 문항 알려주는 것 추가 예정
        
        if(showPwd&&(!/^[A-Za-z0-9]{6,12}$/.test(pw))){
            alert('영문, 숫자 포함 6-12자리의 비밀번호를 입력해주세요.');
            return;
        }
        if(!chkNickname){
            alert('닉네임 중복확인을 완료해주세요.');
            return;
        }
        axios.post('url을 입력해주세요', {
                email: email,
                id:id,
                password: pw,
                nickname:nickname,
                address:fullAddress,
                showAddress:showAddress,
            }
          ).then( (res) => {
            console.log(res);
          })
          .catch( (error)=> {
           console.log(error);
          });
    
       alert('가입을 환영합니다!');
        
    }
};
    const onChangeChkPw=(e)=>{
        if(!pw) {
            alert('비밀번호를 먼저 입력해주세요.');
            setPwChk('');
        }
        else{
        setPwError(e.target.value!==pw);
        setPwChk(e.target.value);
        }
    };
    const [isModal, setModal] = useState(false);
    const modal=(e)=>{
        e.preventDefault();
        if(isModal) setModal(false);
        else setModal(true);
    };

    const onClickChk=(e)=>{
        e.preventDefault();
        if(e.target.value==='nickname'){ //닉네임 정보 받아오는 곳
            if(!nickname) {
                alert('입력창에 먼저 입력해주세요.');
                return;
            }
            else{ //여기에서 axios 통신하시고, 아래 주석처리된 if문 괄호도 채워서 주석 해제해주세요
                axios.get('')
                .then((res)=> { //성공적으로 통신했을 때
